package system

import (
	"IM-Server/global"
	"IM-Server/res"
	"IM-Server/utils/jwts"
	"context"
	"encoding/json"
	"fmt"
	"github.com/gin-gonic/gin"
	"math/rand"
	"net/http"
	"time"
)

type Guest struct {
	ID       int64     `json:"uid"`       // 用户ID
	Nickname string    `json:"nickname"`  // 用户昵称
	Avatar   string    `json:"avatar"`    // 用户头像
	CreateAt time.Time `json:"create_at"` // 创建时间
	UpdateAt time.Time `json:"update_at"` // 更新时间
}

// 预设的随机头像链接数组
var randomAvatars = []string{"data:image/jpeg;base64,:image/jpeg;base64,", "https://th.bing.com/th/id/OIP.Z7BO3qH2w4E5xu_QWD8yegHaEK?w=268&h=187&c=7&r=0&o=5&dpr=1.3&pid=1.7", "https://th.bing.com/th/id/OIP.Mz9FkfM5VjwJctKKgo0PjAHaEg?w=269&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7"}

func (s SysPublicController) VisitorLogin(c *gin.Context) {
	var cr struct {
		Nickname string `form:"nickname" json:"nickname"`
	} // 前端可以通过表单或JSON提交昵称

	// 绑定请求数据，如果昵称未提供，则为空字符串
	if err := c.ShouldBind(&cr); err != nil {
		res.FailWithError(err, nil, c)
		return
	}

	// 生成临时用户ID
	temporaryID := time.Now().UnixNano()

	// 设置昵称，若前端未提供则自动生成一个
	nickname := cr.Nickname
	if nickname == "" {
		nickname = fmt.Sprintf("Visitor_%d", temporaryID)
	}

	// 设置随机头像
	avatar := randomAvatars[rand.Intn(len(randomAvatars))]

	// 创建游客对象
	guest := &Guest{
		ID:       temporaryID,
		Nickname: nickname,
		Avatar:   avatar,
		CreateAt: time.Now(),
		UpdateAt: time.Now(),
	}

	// 模拟生成token
	token, err := jwts.GenToken(jwts.JwtPayLoad{
		NickName: guest.Nickname,
		UserID:   guest.ID,
		Avatar:   guest.Avatar,
	})

	if err != nil {
		global.Logger.Error(err.Error())
		c.JSON(http.StatusInternalServerError, gin.H{"error": "无法生成游客令牌"})
		return
	}

	// 将游客信息序列化成JSON字符串
	guestJSON, err := json.Marshal(guest)
	if err != nil {
		global.Logger.Error(err.Error())
		c.JSON(http.StatusInternalServerError, gin.H{"error": "无法序列化游客信息"})
		return
	}

	// 设置一个键值对，键是 token，值是游客信息(这里我设置的东西是永不删除可以根据实际情况再改生存时间)
	err = global.Redis.Set(context.Background(), token, guestJSON, 0).Err()
	if err != nil {
		c.JSON(http.StatusInternalServerError, gin.H{"error": "无法保存游客信息至缓存"})
		return
	}
	// 登录成功，返回游客信息及生成的token
	res.OkWithData(map[string]interface{}{
		"guest": guest,
		"token": token,
	}, c)
}
